function getCheckboxItem(D) {
    var E = "";
    var F = document.getElementsByName(D);
    if (F.value) {
        return F.value
    }
    for (i = 0; i < F.length; i++) {
        if (F[i].checked) {
            if (E == "") {
                E = F[i].value
            } else {
                E = E + "`" + F[i].value
            }
        }
    }
    return E
};


//弹出URL的网页框
function getLayerBox(title, url) {
    layer.open(
        {
            type: 2,
            title: title,
            content: url
        }
    );
}

//弹窗显示图片
function showpic(url) {
    var content_str="<div style='padding: 4%; line-height: 22px; font-size: 14px; background-color: #393D49; color: #fff;' class='text-center'>" +
        "<div style='margin: 2%;'> <img src='"+url+"' style='max-width: 100%;'> </div></div>";
    layer.open({
        type: 1,
        title: false, //不显示标题栏
        closeBtn: true,
        area: '300px',
        offset: '30px',
        shade: 0.8,
        id: 'LAY_layuipro', //设定一个id，防止重复弹出
        resize: false,
        btnAlign: 'c',
        moveType: 1, //拖拽模式，0或者1
        content: content_str
    });
}
